import React from "react";
// TODO: import useFormik from formik library
import {Form, useFormik} from 'formik'

function App() {
  // TODO: add a const called formik assigned to useFormik()
  const formik = useFormik({
    initialValues: {
      username: '',
      password: ''
    },
    onSubmit: values => {
      alert("Login Successful");
    },
    validate: values => {
      let errors = {};

      if(!values.username){
        errors.username = "Field required"
      }else if(!values.username.includes('@') || !values.username.includes(".")){
        errors.username = "Username should be an email";
      }

      if(!values.username.includes('com') &&
      !values.username.includes('net') &&
      !values.username.includes('fr') &&
      !values.username.includes('br') &&
      !values.username.includes('de')){
        errors.username = "Username should be an email";
      }

      if(!values.password) errors.password = "Field required";

      return errors;
    }
  })

  return (
    <div>
      <>
      <h1>Login</h1>
      <form onSubmit={formik.handleSubmit}>
        <h3>Username</h3>
        <input name="username" type="text" onChange={formik.handleChange} 
        values={formik.values.username}/>
        {formik.errors.username ? <div style={{color: 'red'}}>{formik.errors.username}</div>: null}
        <h3>Password</h3>
        <input name="password" type="text" onChange={formik.handleChange} 
        values={formik.values.password}/>
        {formik.errors.password ? <div style={{color: 'red'}}>{formik.errors.password}</div>: null}
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
      </>
    </div>
  );
}

export default App;
